mapper.select:
select c.*
,(select count(1) from cus_success where successuserid=#{userid} and customerid=c.id and status in(1,3)) successcount ,(select followdate from cus_follow where userid=#{userid} and customerid=c.id order by followdate desc limit 0,1) followdate
from cus_customer c where c.customerstatus in (1,2)
and c.customername like CONCAT('%',#{customername},'%')
and c.mobile like CONCAT('%',#{mobile},'%')
and c.idcard like CONCAT('%',#{idcard},'%')
and c.customertype = #{customertype}
and c.customerstatus = #{customerstatus}
and c.datasources = #{datasources}
and c.importremark like CONCAT('%',#{importremark},'%')
and c.followuserid=#{userid}
having 1=1
and followdate >=CONCAT(#{beginTime},' 00:00:00')
and followdate <=CONCAT(#{endTime},' 23:59:59')
order by followdate desc
