mapper.select:
select c.*,s.customerid, s.successdate, s.successuserid, s.bank, s.amount, s.remark, s.status, s.checkuserid, s.checkdate, s.id successid from cus_customer c, cus_success s where c.id=s.customerid and c.customerstatus in (1,2,3)
and c.customername like CONCAT('%',#{customername},'%')
and c.mobile like CONCAT('%',#{mobile},'%')
and c.idcard like CONCAT('%',#{idcard},'%')
and c.customertype = #{customertype}
and c.customerstatus = #{customerstatus}
and c.datasources = #{datasources}
and c.importremark like CONCAT('%',#{importremark},'%')
and s.successdate >=CONCAT(#{beginTime},' 00:00:00')
and s.successdate <=CONCAT(#{endTime},' 23:59:59')
and s.status = #{status}
and s.successuserid = #{userid}
order by s.successdate desc
